#
# TABLE STRUCTURE FOR: captcha
#

DROP TABLE IF EXISTS captcha;

CREATE TABLE `captcha` (
  `captcha_id` bigint(13) unsigned NOT NULL AUTO_INCREMENT,
  `captcha_time` int(10) unsigned NOT NULL,
  `ip_address` varchar(16) NOT NULL DEFAULT '0',
  `word` varchar(20) NOT NULL,
  PRIMARY KEY (`captcha_id`),
  KEY `word` (`word`)
) ENGINE=InnoDB AUTO_INCREMENT=927 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_answers
#

DROP TABLE IF EXISTS qu_answers;

CREATE TABLE `qu_answers` (
  `pk_answer` bigint(20) NOT NULL AUTO_INCREMENT,
  `question_id` bigint(20) NOT NULL,
  `answer` longtext NOT NULL,
  `image` varchar(255) DEFAULT NULL,
  `status` enum('CORECT','FALSE') NOT NULL,
  `order` int(255) NOT NULL,
  PRIMARY KEY (`pk_answer`)
) ENGINE=MyISAM AUTO_INCREMENT=485 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_answers_type
#

DROP TABLE IF EXISTS qu_answers_type;

CREATE TABLE `qu_answers_type` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `answer_type` varchar(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_categories
#

DROP TABLE IF EXISTS qu_categories;

CREATE TABLE `qu_categories` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `category_name` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=83 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_client_groups_assoc
#

DROP TABLE IF EXISTS qu_client_groups_assoc;

CREATE TABLE `qu_client_groups_assoc` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `client_id` bigint(20) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=104 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_email_assoc
#

DROP TABLE IF EXISTS qu_email_assoc;

CREATE TABLE `qu_email_assoc` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `template_name` varchar(255) NOT NULL,
  `id_template` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=61 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_email_template
#

DROP TABLE IF EXISTS qu_email_template;

CREATE TABLE `qu_email_template` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `email_subject` longtext NOT NULL,
  `email_template_header` longtext NOT NULL,
  `email_template_content` longtext NOT NULL,
  `email_template_footer` longtext NOT NULL,
  `last_updated` date NOT NULL,
  `added` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=20 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_how_its_work
#

DROP TABLE IF EXISTS qu_how_its_work;

CREATE TABLE `qu_how_its_work` (
  `id` int(255) NOT NULL AUTO_INCREMENT,
  `img1` varchar(255) NOT NULL,
  `img2` varchar(255) NOT NULL,
  `text1` varchar(255) NOT NULL,
  `text2` varchar(255) NOT NULL,
  `h1` varchar(255) NOT NULL,
  `order` int(255) NOT NULL,
  `status` enum('0','1') NOT NULL,
  `language` enum('ro','en') NOT NULL,
  `updated` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_inbox
#

DROP TABLE IF EXISTS qu_inbox;

CREATE TABLE `qu_inbox` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `email` varchar(255) NOT NULL,
  `message` longtext NOT NULL,
  `status` int(10) NOT NULL,
  `data` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_members
#

DROP TABLE IF EXISTS qu_members;

CREATE TABLE `qu_members` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `client_id` bigint(20) NOT NULL,
  `member_email` varchar(255) NOT NULL,
  `group_id` bigint(20) NOT NULL,
  `quize_id` int(255) NOT NULL,
  `dispatched` int(255) NOT NULL,
  `added` date NOT NULL,
  `last_updated` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=110 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_messages
#

DROP TABLE IF EXISTS qu_messages;

CREATE TABLE `qu_messages` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `client_id` bigint(20) NOT NULL,
  `sender_id` bigint(20) NOT NULL,
  `subject` varchar(255) NOT NULL,
  `message` text NOT NULL,
  `status` enum('0','1') NOT NULL,
  `data` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=75 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_questions
#

DROP TABLE IF EXISTS qu_questions;

CREATE TABLE `qu_questions` (
  `pk_question` bigint(20) NOT NULL AUTO_INCREMENT,
  `quiz_id` bigint(20) NOT NULL,
  `question` longtext NOT NULL,
  `answer_type` int(255) NOT NULL,
  `image` varchar(255) DEFAULT NULL,
  `order` int(255) NOT NULL,
  PRIMARY KEY (`pk_question`)
) ENGINE=MyISAM AUTO_INCREMENT=189 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_quiz
#

DROP TABLE IF EXISTS qu_quiz;

CREATE TABLE `qu_quiz` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `category_id` int(255) NOT NULL,
  `quiz_name` varchar(255) NOT NULL,
  `answer_type` int(11) NOT NULL,
  `complete_time` int(255) DEFAULT NULL,
  `added` date NOT NULL,
  `last_update` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=81 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_quiz_assoc
#

DROP TABLE IF EXISTS qu_quiz_assoc;

CREATE TABLE `qu_quiz_assoc` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `quiz_id` bigint(20) NOT NULL,
  `client_id` bigint(20) NOT NULL,
  `status` enum('created','attribuited') NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=166 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_quiz_message
#

DROP TABLE IF EXISTS qu_quiz_message;

CREATE TABLE `qu_quiz_message` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `client_id` bigint(20) NOT NULL,
  `member_id` bigint(20) NOT NULL,
  `quiz_id` bigint(20) NOT NULL,
  `score` float NOT NULL,
  `completed_time` float NOT NULL,
  `completed_data` date NOT NULL,
  `status` enum('0','1') NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: qu_users
#

DROP TABLE IF EXISTS qu_users;

CREATE TABLE `qu_users` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `email` varchar(255) NOT NULL,
  `password` varchar(255) NOT NULL,
  `full_name` varchar(255) NOT NULL,
  `admin` int(2) NOT NULL,
  `status` int(2) NOT NULL,
  `added` datetime NOT NULL,
  `last_login` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `acctivation_key` varchar(255) NOT NULL,
  `emails_permited` bigint(20) NOT NULL,
  `emails_sent` int(255) NOT NULL,
  `first_login` enum('0','1') NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=414 DEFAULT CHARSET=latin1;



